mapper.insert:
insert into game_gift (gift_id, gift_code, gift_name, 
  gift_type, total_quantity, remaining_quantity, 
  start_time, end_time, is_active, 
  create_time, update_time, description
  )
values (#{giftId,jdbcType=BIGINT}, #{giftCode,jdbcType=VARCHAR}, #{giftName,jdbcType=VARCHAR}, 
  #{giftType,jdbcType=TINYINT}, #{totalQuantity,jdbcType=INTEGER}, #{remainingQuantity,jdbcType=INTEGER}, 
  #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{isActive,jdbcType=TINYINT}, 
  #{createTime,jdbcType=TIMESTAMP}, #{updateTime,jdbcType=TIMESTAMP}, #{description,jdbcType=LONGVARCHAR}
  )

mapper.select:
SELECT * FROM game_gift
WHERE is_active = 1
AND start_time <= #{now}
AND end_time >= #{now}
AND (remaining_quantity = -1 OR remaining_quantity > 0)
