mapper.select:
select * from user



        and username like #{username}


        and roleId = #{roleId}


        and isStart = #{isStart}


        and referCode like #{referCode}


        and referId = #{referId}


        and userType = #{userType}


order by createTime desc

select count(1) from user



        and username like CONCAT('%',#{username},'%')


        and roleId = #{roleId}


        and isStart = #{isStart}


        and referCode like CONCAT('%',#{referCode},'%')


        and referId = #{referId}


        and userType = #{userType}

mapper.update:
update user
username = #{username}, userpassword = #{userpassword}, userpassword2 = #{userpassword2}, sex = #{sex}, birthday = #{birthday}, cardType = #{cardType}, cardTypeName = #{cardTypeName}, idCard = #{idCard}, country = #{country}, mobile = #{mobile}, email = #{email}, userAddress = #{userAddress}, postCode = #{postCode}, createTime = #{createTime}, referId = #{referId}, referCode = #{referCode}, roleId = #{roleId}, roleName = #{roleName}, userType = #{userType}, userTypeName = #{userTypeName}, isStart = #{isStart}, lastUpdateTime = #{lastUpdateTime}, lastLoginTime = #{lastLoginTime}, bankName = #{bankName}, accountHolder = #{accountHolder}, bankAccount = #{bankAccount}, idCardPicPath = #{idCardPicPath}, bankPicPath = #{bankPicPath}
where id = #{id}

update user
idCardPicPath = null, bankPicPath = null
where userId = #{userId}

