INSERT INTO `blog` VALUES (13, 1, 'LambdaQueryWrapper中常用的方法', '<p data-we-empty-p=\"\" style=\"text-align:center;\"><img src=\"/common/static/blog/20211009/7b199fad34be443782543ae929e9bf0a.jpg\" style=\"max-width:100%;\" contenteditable=\"false\"/><br/><br/></p><ol style=\"padding-left: 40px; margin-top: 0px; margin-bottom: 0px; margin-left: 0px; color: rgb(51, 51, 51); font-family: verdana, Arial, Helvetica, sans-serif; font-size: 14px;\"><li style=\"list-style: decimal;\"><b>setSqlSelect&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;设置 SELECT 查询字段</b></li><li style=\"list-style: decimal;\"><b>where&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; WHERE 语句，拼接 + WHERE 条件</b></li><li style=\"list-style: decimal;\"><b>and&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; AND 语句，拼接 + AND 字段=值</b></li><li style=\"list-style: decimal;\"><b>and&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; New AND 语句，拼接 + AND (字段=值)</b></li><li style=\"list-style: decimal;\"><b>or&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;OR 语句，拼接 + OR 字段=值</b></li><li style=\"list-style: decimal;\"><b>orNew&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; OR 语句，拼接 + OR (字段=值)</b></li><li style=\"list-style: decimal;\"><b>eq&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 等于=</b></li><li style=\"list-style: decimal;\"><b>allEq&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 基于 map 内容等于=</b></li><li style=\"list-style: decimal;\"><b>ne&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 不等于&lt;&gt;</b></li><li style=\"list-style: decimal;\"><b>gt&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;大于&gt;</b></li><li style=\"list-style: decimal;\"><b>ge&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 大于等于&gt;=</b></li><li style=\"list-style: decimal;\"><b>lt&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 小于&lt;</b></li><li style=\"list-style: decimal;\"><b>le&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;小于等于&lt;=</b></li><li style=\"list-style: decimal;\"><b>like&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 模糊查询 LIKE</b></li><li style=\"list-style: decimal;\"><b>notLike&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 模糊查询 NOT LIKE</b></li><li style=\"list-style: decimal;\"><b>in&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;IN 查询</b></li><li style=\"list-style: decimal;\"><b>notIn&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NOT IN 查询</b></li><li style=\"list-style: decimal;\"><b>isNull&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;NULL 值查询</b></li><li style=\"list-style: decimal;\"><b>isNotNull&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; IS NOT NULL</b></li><li style=\"list-style: decimal;\"><b>groupBy&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;分组 GROUP BY</b></li><li style=\"list-style: decimal;\"><b>having&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; HAVING 关键词</b></li><li style=\"list-style: decimal;\"><b>orderBy&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 排序 ORDER BY</b></li><li style=\"list-style: decimal;\"><b>orderAsc&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; ASC 排序 ORDER BY</b></li><li style=\"list-style: decimal;\"><b>orderDesc&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; DESC 排序 ORDER BY</b></li><li style=\"list-style: decimal;\"><b>exists&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;EXISTS 条件语句</b></li><li style=\"list-style: decimal;\"><b>notExists&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NOT EXISTS 条件语句</b></li><li style=\"list-style: decimal;\"><b>between&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;BETWEEN 条件语句</b></li><li style=\"list-style: decimal;\"><b>notBetween&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; NOT BETWEEN 条件语句</b></li><li style=\"list-style: decimal;\"><b>addFilter&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 自由拼接 SQL</b></li><li style=\"list-style: decimal;\"><b>last&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; 拼接在最后，例如：last(“LIMIT 1”)</b></li></ol><div><font color=\"#333333\" face=\"verdana, Arial, Helvetica, sans-serif\"><span style=\"font-size: 14px;\"><b><br/></b></span></font></div><div><h1 style=\"outline: 0px; margin-top: 0px; margin-bottom: 0px; font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; font-size: 28px; overflow-wrap: break-word; color: rgb(34, 34, 38); word-break: break-all; font-variant-ligatures: common-ligatures;\">Mybatis-Plus中正确使用sum聚合函数</h1></div><pre><code class=\"Bash\"><span class=\"hljs-comment\">//求某人的成绩和。</span>\nQueryWrapper<span class=\"hljs-subst\">&lt;</span>People<span class=\"hljs-subst\">&gt;</span> queryWrapper <span class=\"hljs-subst\">=</span> <span class=\"hljs-literal\">new</span> QueryWrapper<span class=\"hljs-subst\">&lt;&gt;</span>();\nqueryWrapper<span class=\"hljs-built_in\">.</span><span class=\"hljs-keyword\">select</span>(<span class=\"hljs-string\">\"IFNULL(sum(score),0) as totalScore\"</span>)\n             <span class=\"hljs-built_in\">.</span><span class=\"hljs-literal\">eq</span>(<span class=\"hljs-string\">\"user_name\"</span>, userName);\n<span class=\"hljs-built_in\">Map</span><span class=\"hljs-subst\">&lt;</span><span class=\"hljs-built_in\">String</span>, Object<span class=\"hljs-subst\">&gt;</span> <span class=\"hljs-built_in\">map</span> <span class=\"hljs-subst\">=</span> this<span class=\"hljs-built_in\">.</span>getMap(queryWrapper);\nBigDecimal sumCount <span class=\"hljs-subst\">=</span> (BigDecimal) <span class=\"hljs-built_in\">map</span><span class=\"hljs-built_in\">.</span>get(<span class=\"hljs-string\">\"totalScore\"</span>);\n</code></pre><p><br/></p>', NULL, 123, '2021-10-09 00:55:58', '1', NULL, NULL, '原创', 'java', '/common/static/blog/20211009/7b199fad34be443782543ae929e9bf0a.jpg', NULL, '0');
INSERT INTO `blog` VALUES (14, 1, 'vue中 关于$emit的用法', '<p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\"><b>1、父组件可以使用 props 把数据传给子组件。<br style=\"outline: 0px; overflow-wrap: break-word;\"/>2、子组件可以使用 $emit,让父组件监听到自定义事件 。</b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\"><b>vm.$emit( event, arg ) //触发当前实例上的事件</b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\"><b>vm.$on( event, fn );//监听event事件后运行 fn；&nbsp;</b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\"><b><br/></b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\">例如：子组件：<b><br/></b></p><pre><code class=\"JavaScript\"><span class=\"xml\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">template</span>&gt;</span>\n  <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">div</span> <span class=\"hljs-attribute\">class</span>=<span class=\"hljs-value\">\"train-city\"</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">h3</span>&gt;</span>父组件传给子组件的toCity:</span><span class=\"hljs-expression\">{{<span class=\"hljs-variable\">sendData</span>}}</span><span class=\"xml\"><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">h3</span>&gt;</span> \n    <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">br</span>/&gt;</span><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">button</span> @<span class=\"hljs-attribute\">click</span>=<span class=\"hljs-value\">\'select(`大连`)\'</span>&gt;</span>点击此处将‘大连’发射给父组件<span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">button</span>&gt;</span>\n  <span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">div</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">template</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">script</span>&gt;</span><span class=\"javascript\">\n  export <span class=\"hljs-keyword\">default</span> {\n    name:<span class=\"hljs-string\">\'trainCity\'</span>,\n    props:[<span class=\"hljs-string\">\'sendData\'</span>], <span class=\"hljs-comment\">// 用来接收父组件传给子组件的数据</span>\n    methods:{\n      select(val) {\n        <span class=\"hljs-keyword\">let</span> data = {\n          cityname: val\n        };\n        <span class=\"hljs-keyword\">this</span>.$emit(<span class=\"hljs-string\">\'showCityName\'</span>,data);<span class=\"hljs-comment\">//select事件触发后，自动触发showCityName事件</span>\n      }\n    }\n  }\n</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">script</span>&gt;</span></span></code></pre><p><span style=\"color: rgb(77, 77, 77); font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">父组件：</span><br/></p><pre><code class=\"JavaScript\"><span class=\"xml\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">template</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">div</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">div</span>&gt;</span>父组件的toCity</span><span class=\"hljs-expression\">{{<span class=\"hljs-variable\">toCity</span>}}</span><span class=\"xml\"><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">div</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">train-city</span> @<span class=\"hljs-attribute\">showCityName</span>=<span class=\"hljs-value\">\"updateCity\"</span> <span class=\"hljs-attribute\">:sendData</span>=<span class=\"hljs-value\">\"toCity\"</span>&gt;</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">train-city</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">div</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">template</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">script</span>&gt;</span><span class=\"javascript\">\n  import TrainCity from <span class=\"hljs-string\">\"./train-city\"</span>;\n  export <span class=\"hljs-keyword\">default</span> {\n    name:<span class=\"hljs-string\">\'index\'</span>,\n    components: {TrainCity},\n    data () {\n      <span class=\"hljs-keyword\">return</span> {\n        toCity:<span class=\"hljs-string\">\"北京\"</span>\n      }\n    },\n    methods:{\n      updateCity(data){<span class=\"hljs-comment\">//触发子组件城市选择-选择城市的事件</span>\n        <span class=\"hljs-keyword\">this</span>.toCity = data.cityname;<span class=\"hljs-comment\">//改变了父组件的值</span>\n        console.log(<span class=\"hljs-string\">\'toCity:\'</span>+<span class=\"hljs-keyword\">this</span>.toCity)\n      }\n    }\n  }\n</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">script</span>&gt;</span></span></code></pre><p><span style=\"color: rgb(77, 77, 77); font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">图一：点击之前的数据</span><br/></p><p><img src=\"/common/static/blog/20211029/1a102ef2a19742649bec22a3751bce39.png\" style=\"max-width:100%;\" contenteditable=\"false\"/></p><p><span style=\"color: rgb(77, 77, 77); font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">图二：点击之后的数据</span><br/></p><p><img src=\"/common/static/blog/20211029/66e8d0ae32ba43d3a02fb5175c4ef4f3.png\" style=\"max-width:100%;\" contenteditable=\"false\"/></p>', NULL, 3, '2021-10-29 05:55:56', NULL, NULL, NULL, '原创', '前端', NULL, NULL, '0');
INSERT INTO `blog` VALUES (23, 1, 'LinkedList作者说他自己都不用LinkedList？看完给我整不会了。。', '<section style=\"padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; letter-spacing: 1px; line-height: 2em; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">今天在网上冲浪，就看到有文章写说 LinkedList 的作者说他自己都不用 LinkedList，感觉既惊讶，又有点意思。</span></section><figure style=\"margin-top: 10px; margin-bottom: 10px; outline: 0px; max-width: 100%; color: rgb(51, 51, 51); letter-spacing: 0.544px; caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; display: flex; flex-direction: column; justify-content: center; align-items: center; overflow-wrap: break-word !important;\"></figure><p style=\"margin-top:0px; margin-bottom:0px; padding-top:10px; padding-bottom:10px; outline:0px; max-width:100%; clear:both; min-height:1em; color:rgb(51, 51, 51); caret-color:rgb(51, 51, 51); font-family:-apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust:auto; line-height:2; letter-spacing:1px; overflow-wrap:break-word !important; text-align:center;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\"><img src=\"/common/static/blog/20211128/870866810a6745a9b00d7203baad4514.jpg\" style=\"max-width:100%;\" contenteditable=\"false\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">可能这就是大佬吧，我造轮子，但是我不用！或者这就是传说中的厨子不吃自己做的菜？</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">不扯了，言归正传。其实我个人觉得大佬说好像是事实，因为在业务上好像都用不到 LinkedList ，大多数场景下都是用 ArrayList 比较合适，我细数了下自己平日里的使用情况，真的都是 ArrayList 。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">说到这，可能有人不同意了，说我可是看过面试题的，LinkedList 可是有它的优势的！</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">这题我也看过，没记错的话应该是：说说 ArrayList 和 LinkedList 的之间区别？</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">我觉得这题可谓之为“八股文前三甲”，其实这题映射过来也就是关于数组与链表的比较。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">只要你在网上看过这道面试题，你看到的答案必然是：</span></p><ul style=\"margin-top: 8px; margin-bottom: 8px; margin-left: 0px; padding-left: 25px; outline: 0px; max-width: 100%; letter-spacing: 0.544px; font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; overflow-wrap: break-word !important;\"><li style=\"outline: 0px; max-width: 100%; font-weight: bold; overflow-wrap: break-word !important;\"><section style=\"margin-top: 5px; margin-bottom: 5px; outline: 0px; max-width: 100%; color: rgb(1, 1, 1); line-height: 2; font-size: 15px; overflow-wrap: break-word !important;\"><strong style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 16px; overflow-wrap: break-word !important;\">数组的随机访问快，插入和删除慢</span></strong></section></li><li style=\"outline: 0px; max-width: 100%; font-weight: bold; overflow-wrap: break-word !important;\"><section style=\"margin-top: 5px; margin-bottom: 5px; outline: 0px; max-width: 100%; color: rgb(1, 1, 1); line-height: 2; font-size: 15px; overflow-wrap: break-word !important;\"><strong style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 16px; overflow-wrap: break-word !important;\">链表的插入删除快，随机访问慢</span></strong></section></li><li style=\"outline: 0px; max-width: 100%; font-weight: bold; overflow-wrap: break-word !important;\"><section style=\"margin-top: 5px; margin-bottom: 5px; outline: 0px; max-width: 100%; color: rgb(1, 1, 1); line-height: 2; font-size: 15px; overflow-wrap: break-word !important;\"><strong style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 16px; overflow-wrap: break-word !important;\">频繁增删的情况下，用链表比较合适</span></strong></section></li><li style=\"outline: 0px; max-width: 100%; font-weight: bold; overflow-wrap: break-word !important;\"><section style=\"margin-top: 5px; margin-bottom: 5px; outline: 0px; max-width: 100%; color: rgb(1, 1, 1); line-height: 2; font-size: 15px; overflow-wrap: break-word !important;\"><strong style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 16px; overflow-wrap: break-word !important;\">在随机查找多的情况下，用数组比较合适</span></strong></section></li></ul><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">问题就出在链表的频繁增删这一点。如果单从增加查这三个方法的时间复杂度来看，确实如此，没有错。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">但是，在平时的使用上来说，这个说法就完全不成立！你想想，如果你要在链表中删除某个元素，你首先得找到它啊！这个链表的查找可耗时的呀！</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">所以在实际使用的时候，如果你有频繁的增删，也不应该用链表。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">不信？我们来做个实验看看咯。</span></p><pre style=\"outline: 0px; max-width: 100%; color: rgb(51, 51, 51); letter-spacing: 0.544px; caret-color: rgb(51, 51, 51); font-size: 16px; text-size-adjust: auto; border-radius: 5px; box-shadow: rgba(0, 0, 0, 0.55) 0px 2px 10px; overflow-wrap: break-word !important;\"><code style=\"margin-right: 0px; margin-left: 0px; padding: 15px 16px 16px; outline: 0px; max-width: 100%; overflow-x: auto; color: rgb(221, 221, 221); display: -webkit-box; font-family: &quot;Operator Mono&quot;, Consolas, Monaco, Menlo, monospace; font-size: 12px; background-color: rgb(39, 40, 34); border-radius: 5px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">public</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">class</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; font-weight: bold; color: white; line-height: 26px; overflow-wrap: break-word !important;\">YesArrayLinkedBattle</span>&nbsp;</span>{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">private</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">static</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">final</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">int</span>&nbsp;COUNT&nbsp;=&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">100000</span>;<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">static</span>&nbsp;List&lt;Integer&gt;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">fillList</span><span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">(List&lt;Integer&gt;&nbsp;list)</span>&nbsp;</span>{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">for</span>&nbsp;(<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">int</span>&nbsp;i&nbsp;=&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">0</span>;&nbsp;i&nbsp;&lt;&nbsp;COUNT;&nbsp;i++)&nbsp;{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;list.add(i);&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(117, 113, 94); line-height: 26px; overflow-wrap: break-word !important;\">//将list填满，假装我们在数据库里得到这么多数据</span><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">return</span>&nbsp;list;<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">static</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">void</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">randomAdd</span><span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">(List&lt;Integer&gt;&nbsp;list,&nbsp;String&nbsp;listType)</span>&nbsp;</span>{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">long</span>&nbsp;t1&nbsp;=&nbsp;System.currentTimeMillis();<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">for</span>&nbsp;(<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">int</span>&nbsp;i&nbsp;=&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">0</span>;&nbsp;i&nbsp;&lt;&nbsp;COUNT;&nbsp;i++)&nbsp;{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;list.add(ThreadLocalRandom.current().nextInt(<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">0</span>,COUNT),&nbsp;i);<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">long</span>&nbsp;t2&nbsp;=&nbsp;System.currentTimeMillis();<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;System.out.println(listType&nbsp;+<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); line-height: 26px; overflow-wrap: break-word !important;\">\"随机位置插入\"</span>&nbsp;+&nbsp;COUNT&nbsp;+&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); line-height: 26px; overflow-wrap: break-word !important;\">\"次耗时：\"</span>&nbsp;+&nbsp;(t2-t1));<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;<span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">public</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">static</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">void</span>&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">main</span><span style=\"outline: 0px; max-width: 100%; line-height: 26px; overflow-wrap: break-word !important;\">(String[]&nbsp;args)</span>&nbsp;</span>{<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;randomAdd(fillList(<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">new</span>&nbsp;ArrayList&lt;&gt;(COUNT)),&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); line-height: 26px; overflow-wrap: break-word !important;\">\"数组\"</span>);<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;randomAdd(fillList(<span style=\"outline: 0px; max-width: 100%; color: rgb(249, 38, 114); font-weight: bold; line-height: 26px; overflow-wrap: break-word !important;\">new</span>&nbsp;LinkedList&lt;&gt;()),&nbsp;<span style=\"outline: 0px; max-width: 100%; color: rgb(166, 226, 46); line-height: 26px; overflow-wrap: break-word !important;\">\"链表\"</span>);<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/><br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>&nbsp;&nbsp;&nbsp;&nbsp;}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/>}<br style=\"outline: 0px; max-width: 100%; overflow-wrap: break-word !important;\"/></code></pre><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">这个实验很粗暴简单，但也很直观，分别对被填满数据的 ArrayList 和 LinkedList 执行 10 万次随机的插入操作，然后分别统计耗时。</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">执行结果如下：</span></p><figure style=\"margin-top: 10px; margin-bottom: 10px; outline: 0px; max-width: 100%; color: rgb(51, 51, 51); letter-spacing: 0.544px; caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; display: flex; flex-direction: column; justify-content: center; align-items: center; overflow-wrap: break-word !important;\"></figure><p style=\"margin-top:0px; margin-bottom:0px; padding-top:10px; padding-bottom:10px; outline:0px; max-width:100%; clear:both; min-height:1em; color:rgb(51, 51, 51); caret-color:rgb(51, 51, 51); font-family:-apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust:auto; line-height:2; letter-spacing:1px; overflow-wrap:break-word !important; text-align:center;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\"><img src=\"/common/static/blog/20211128/7342c3aa661d48e2ac5898bcaea5ebce.jpg\" style=\"max-width:100%;\" contenteditable=\"false\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">是吧，在随机插入的情况下，链表不占优势反而大弱于数组！</span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">所以说对于链表的插入操作，不能只关注其插入的时间复杂度，也要算上查找到前节点的开销，因此不能武断地说：</span><code style=\"margin-right: 2px; margin-left: 2px; padding: 2px 6px; outline: 0px; max-width: 100%; font-size: 14px; border-radius: 4px; background-color: rgba(27, 31, 35, 0.05); font-family: &quot;Operator Mono&quot;, Consolas, Monaco, Menlo, monospace; color: rgb(19, 148, 216); word-break: normal; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">频繁增删的情况下，用链表比较合适</span></code></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">当然，如果数据量很小的话，其实两者都是差不多的，比如长度都为 100 ，执行 100 次，则耗时如下：</span></p><figure style=\"margin-top: 10px; margin-bottom: 10px; outline: 0px; max-width: 100%; color: rgb(51, 51, 51); letter-spacing: 0.544px; caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; display: flex; flex-direction: column; justify-content: center; align-items: center; overflow-wrap: break-word !important;\"></figure><p style=\"margin-top:0px; margin-bottom:0px; padding-top:10px; padding-bottom:10px; outline:0px; max-width:100%; clear:both; min-height:1em; color:rgb(51, 51, 51); caret-color:rgb(51, 51, 51); font-family:-apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust:auto; line-height:2; letter-spacing:1px; overflow-wrap:break-word !important; text-align:center;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\"><img src=\"/common/static/blog/20211128/b5d3913229854eb9bc5dba49be2b564d.jpg\" style=\"max-width:100%;\" contenteditable=\"false\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">长度都为 1000 ，执行 1000 次，则耗时如下：</span></p><figure style=\"margin-top: 10px; margin-bottom: 10px; outline: 0px; max-width: 100%; color: rgb(51, 51, 51); letter-spacing: 0.544px; caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; font-size: 16px; text-size-adjust: auto; display: flex; flex-direction: column; justify-content: center; align-items: center; overflow-wrap: break-word !important;\"></figure><p style=\"margin-top:0px; margin-bottom:0px; padding-top:10px; padding-bottom:10px; outline:0px; max-width:100%; clear:both; min-height:1em; color:rgb(51, 51, 51); caret-color:rgb(51, 51, 51); font-family:-apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust:auto; line-height:2; letter-spacing:1px; overflow-wrap:break-word !important; text-align:center;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\"><img src=\"/common/static/blog/20211128/2984392fef4b4d2793000c5c9ec29e32.jpg\" style=\"max-width:100%;\" contenteditable=\"false\"/></span></p><p style=\"margin-top: 0px; margin-bottom: 0px; padding-top: 10px; padding-bottom: 10px; outline: 0px; max-width: 100%; clear: both; min-height: 1em; color: rgb(51, 51, 51); caret-color: rgb(51, 51, 51); font-family: -apple-system, system-ui, BlinkMacSystemFont, &quot;Helvetica Neue&quot;, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, Arial, sans-serif; text-size-adjust: auto; line-height: 2; letter-spacing: 1px; overflow-wrap: break-word !important;\"><span style=\"outline: 0px; max-width: 100%; font-size: 17px; overflow-wrap: break-word !important;\">所以，在数据量不大且操作次数不多的情况其实不必过于纠结到底用哪个。但在数据量较大且对时延敏感的情况下，建议还是做好测试，不能平白的根据一些“网上结论”而下定论。</span></p>', NULL, 16, '2021-11-28 22:25:57', '', NULL, NULL, '原创', 'java', NULL, NULL, '0');
INSERT INTO `blog` VALUES (25, 1, 'uni-app &amp;nbsp;转义多空格失效解决方法', '<p>在对于div的css样式中添加</p><pre><code class=\"CSS\"><span class=\"hljs-attribute\">white-space</span><span class=\"hljs-value\">: pre-wrap;</span></code></pre>', NULL, 3, '2021-11-30 14:54:01', '', NULL, NULL, '原创', '前端', NULL, NULL, '0');
INSERT INTO `blog` VALUES (27, 1, 'vue子组件给父组件传值', '<p>子组件：</p><pre><code class=\"Bash\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">template</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">div</span> <span class=\"hljs-attribute\">class</span>=<span class=\"hljs-value\">\"app\"</span>&gt;</span>\n       <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">input</span> @<span class=\"hljs-attribute\">click</span>=<span class=\"hljs-value\">\"sendMsg\"</span> <span class=\"hljs-attribute\">type</span>=<span class=\"hljs-value\">\"button\"</span> <span class=\"hljs-attribute\">value</span>=<span class=\"hljs-value\">\"给父组件传递值\"</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">div</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">template</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">script</span>&gt;</span><span class=\"javascript\">\nexport <span class=\"hljs-keyword\">default</span> {\n \n    data () {\n        <span class=\"hljs-keyword\">return</span> {\n            <span class=\"hljs-comment\">//将msg传递给父组件</span>\n            msg: <span class=\"hljs-string\">\"我是子组件的msg\"</span>,\n        }\n    },\n     methods:{\n         sendMsg(){\n             <span class=\"hljs-comment\">//func: 是父组件指定的传数据绑定的函数，this.msg:子组件给父组件传递的数据</span>\n             <span class=\"hljs-keyword\">this</span>.$emit(<span class=\"hljs-string\">\'func\'</span>,<span class=\"hljs-keyword\">this</span>.msg)\n         }\n     }\n}\n</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">script</span>&gt;</span></code></pre><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\">子组件通过<span style=\"outline: 0px; font-weight: 700; overflow-wrap: break-word;\">this.$emit()</span>的方式将值传递给父组件</p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\"><span style=\"outline: 0px; font-weight: 700; overflow-wrap: break-word;\">注意：</span>这里的func是父组件中绑定的函数名</p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; color: rgb(77, 77, 77); overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 26px !important;\">父组件：</p><pre><code class=\"Bash\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">template</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">div</span> <span class=\"hljs-attribute\">class</span>=<span class=\"hljs-value\">\"app\"</span>&gt;</span>\n        <span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">child</span> @<span class=\"hljs-attribute\">func</span>=<span class=\"hljs-value\">\"getMsgFormSon\"</span>&gt;</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">child</span>&gt;</span>\n    <span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">div</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">template</span>&gt;</span>\n<span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">script</span>&gt;</span><span class=\"javascript\">\nimport child from <span class=\"hljs-string\">\'./child.vue\'</span>\nexport <span class=\"hljs-keyword\">default</span> {\n    data () {\n        <span class=\"hljs-keyword\">return</span> {\n            msgFormSon: <span class=\"hljs-string\">\"this is msg\"</span>\n        }\n    },\n    components:{\n        child,\n    },\n    methods:{\n            getMsgFormSon(data){\n                <span class=\"hljs-keyword\">this</span>.msgFormSon = data\n                console.log(<span class=\"hljs-keyword\">this</span>.msgFormSon)\n            }\n    }\n}\n</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">script</span>&gt;</span></code></pre>', NULL, 15, '2021-12-07 11:26:59', '', NULL, NULL, '原创', '前端', NULL, NULL, '0');
INSERT INTO `blog` VALUES (33, 1, 'MySQL查询当天当月数据', '<p>今天<br/><br/>select * from 表名 where to_days(时间字段名) = to_days(now());<br/><br/>昨天<br/><br/>SELECT * FROM 表名 WHERE TO_DAYS( NOW( ) ) - TO_DAYS( 时间字段名) &lt;= 1<br/><br/>近7天<br/><br/>SELECT * FROM 表名 where DATE_SUB(CURDATE(), INTERVAL 7 DAY) &lt;= date(时间字段名)<br/><br/>近30天<br/><br/>SELECT * FROM 表名 where DATE_SUB(CURDATE(), INTERVAL 30 DAY) &lt;= date(时间字段名)<br/><br/>本月<br/><br/>SELECT * FROM 表名 WHERE DATE_FORMAT( 时间字段名, \'%Y%m\' ) = DATE_FORMAT( CURDATE( ) , \'%Y%m\' )<br/><br/>上一月<br/><br/>SELECT * FROM 表名 WHERE PERIOD_DIFF( date_format( now( ) , \'%Y%m\' ) ,date_format( 时间字段名, \'%Y%m\' ) ) =1<br/><br/>查询本季度数据<br/><br/>select * from `ht_invoice_information` where QUARTER(create_date)=QUARTER(now());<br/><br/>查询上季度数据<br/><br/>select * from `ht_invoice_information` where QUARTER(create_date)=QUARTER(DATE_SUB(now(),interval 1 QUARTER));<br/><br/>查询本年数据<br/><br/>select * from `ht_invoice_information` where YEAR(create_date)=YEAR(NOW());<br/><br/>查询上年数据<br/><br/>select * from `ht_invoice_information` where year(create_date)=year(date_sub(now(),interval 1 year));<br/><br/>查询当前这周的数据<br/><br/>SELECT name,submittime FROMenterprise WHERE YEARWEEK(date_format(submittime,\'%Y-%m-%d\')) = YEARWEEK(now());<br/><br/>查询上周的数据<br/><br/>SELECT name,submittime FROMenterprise WHERE YEARWEEK(date_format(submittime,\'%Y-%m-%d\')) = YEARWEEK(now())-1;<br/><br/>查询上个月的数据<br/><br/><br/>select name,submittime fromenterprise where date_format(submittime,\'%Y-%m\')=date_format(DATE_SUB(curdate(), INTERVAL 1 MONTH),\'%Y-%m\')<br/><br/> <br/><br/>select * from user where DATE_FORMAT(pudate,\'%Y%m\') = DATE_FORMAT(CURDATE(),\'%Y%m\') ;<br/><br/> <br/><br/>select * from user where WEEKOFYEAR(FROM_UNIXTIME(pudate,\'%y-%m-%d\')) = WEEKOFYEAR(now())<br/><br/> <br/><br/>select * from user where MONTH(FROM_UNIXTIME(pudate,\'%y-%m-%d\')) = MONTH(now())<br/><br/> <br/><br/>select * from user where YEAR(FROM_UNIXTIME(pudate,\'%y-%m-%d\')) = YEAR(now()) and MONTH(FROM_UNIXTIME(pudate,\'%y-%m-%d\')) = MONTH(now())<br/><br/>select * from user where pudate between  上月最后一天 and 下月第一天<br/><br/><br/>查询当前月份的数据 <br/><br/>select name,submittime fromenterprise   wheredate_format(submittime,\'%Y-%m\')=date_format(now(),\'%Y-%m\')<br/><br/>查询距离当前现在6个月的数据<br/><br/>select name,submittime fromenterprise where submittime betweendate_sub(now(),interval 6 month) and now();<br/>————————————————<br/>版权声明：本文为CSDN博主「啊我的眼睛」的原创文章，遵循CC 4.0 BY-SA版权协议，转载请附上原文出处链接及本声明。<br/>原文链接：https://blog.csdn.net/as00v1/article/details/54571361</p>', NULL, 3, '2021-12-14 15:34:24', '', NULL, NULL, '原创', '数据库', NULL, NULL, '0');
