INSERT INTO `blog` VALUES (91, 1, 'Hyper-V安装命令.cmd', '<pre><code class=\"Bash\">pushd <span class=\"hljs-string\">\"%~dp0\"</span>\ndir /b <span class=\"hljs-subst\">%</span>SystemRoot<span class=\"hljs-subst\">%\\</span>servicing<span class=\"hljs-subst\">\\</span>Packages<span class=\"hljs-subst\">\\*</span>Hyper<span class=\"hljs-attribute\">-V</span><span class=\"hljs-subst\">*</span><span class=\"hljs-built_in\">.</span>mum <span class=\"hljs-subst\">&gt;</span>hyper<span class=\"hljs-attribute\">-v</span><span class=\"hljs-built_in\">.</span>txt\nfor /f <span class=\"hljs-subst\">%%</span>i <span class=\"hljs-keyword\">in</span> (<span class=\"hljs-string\">\'findstr /i . hyper-v.txt 2^&gt;nul\'</span>) <span class=\"hljs-keyword\">do</span> dism /online /norestart /add<span class=\"hljs-attribute\">-package</span>:<span class=\"hljs-string\">\"%SystemRoot%\\servicing\\Packages\\%%i\"</span>\ndel hyper<span class=\"hljs-attribute\">-v</span><span class=\"hljs-built_in\">.</span>txt\nDism /online /enable<span class=\"hljs-attribute\">-feature</span> /featurename:Microsoft<span class=\"hljs-attribute\">-Hyper</span><span class=\"hljs-attribute\">-V</span><span class=\"hljs-attribute\">-All</span> /LimitAccess /<span class=\"hljs-literal\">ALL</span>\n</code></pre>', NULL, 12, '2022-12-13 20:53:04', NULL, NULL, NULL, '原创', '运维', NULL, NULL, '0');
INSERT INTO `blog` VALUES (92, 1, 'String、Date、Calendar之间的转换', '<pre><code class=\"Bash\"><span class=\"hljs-number\">1.</span>Calendar 转化 <span class=\"hljs-built_in\">String</span>\n\nCalendar calendat = Calendar.getInstance();\n\nSimpleDateFormat sdf = <span class=\"hljs-keyword\">new</span> SimpleDateFormat(<span class=\"hljs-string\">\"yyyy-MM-dd\"</span>);\n\n<span class=\"hljs-built_in\">String</span> dateStr = sdf.format(calendar.getTime());\n\n \n\n<span class=\"hljs-number\">2.</span><span class=\"hljs-built_in\">String</span> 转化Calendar\n\n<span class=\"hljs-built_in\">String</span> str=<span class=\"hljs-string\">\"2012-5-27\"</span>;\n\nSimpleDateFormat sdf= <span class=\"hljs-keyword\">new</span> SimpleDateFormat(<span class=\"hljs-string\">\"yyyy-MM-dd\"</span>);\n\n<span class=\"hljs-built_in\">Date</span> <span class=\"hljs-built_in\">date</span> =sdf.parse(str);\n\nCalendar calendar = Calendar.getInstance();\n\ncalendar.setTime(<span class=\"hljs-built_in\">date</span>);\n\n \n\n<span class=\"hljs-number\">3.</span><span class=\"hljs-built_in\">Date</span> 转化<span class=\"hljs-built_in\">String</span>\n\nSimpleDateFormat sdf= <span class=\"hljs-keyword\">new</span> SimpleDateFormat(<span class=\"hljs-string\">\"yyyy-MM-dd\"</span>);\n\n<span class=\"hljs-built_in\">String</span> dateStr=sdf.format(<span class=\"hljs-keyword\">new</span> <span class=\"hljs-built_in\">Date</span>());\n\n \n\n<span class=\"hljs-number\">4.</span><span class=\"hljs-built_in\">String</span> 转化<span class=\"hljs-built_in\">Date</span>\n\n<span class=\"hljs-built_in\">String</span> str=<span class=\"hljs-string\">\"2012-5-27\"</span>;\n\nSimpleDateFormat sdf= <span class=\"hljs-keyword\">new</span> SimpleDateFormat(<span class=\"hljs-string\">\"yyyy-MM-dd\"</span>);\n\n<span class=\"hljs-built_in\">Date</span> <span class=\"hljs-built_in\">date</span>= sdf.parse(str);\n\n \n\n<span class=\"hljs-number\">5.</span><span class=\"hljs-built_in\">Date</span> 转化Calendar\n\nCalendar calendar = Calendar.getInstance();\n\ncalendar.setTime(<span class=\"hljs-keyword\">new</span> java.util.<span class=\"hljs-built_in\">Date</span>());\n\n \n\n<span class=\"hljs-number\">6.</span>Calendar转化<span class=\"hljs-built_in\">Date</span>\n\nCalendar calendar = Calendar.getInstance();\n\njava.util.<span class=\"hljs-built_in\">Date</span> <span class=\"hljs-built_in\">date</span> =calendar.getTime();</code></pre>', NULL, 20, '2022-12-15 11:14:36', '2', NULL, NULL, '原创', 'java', '/common/static/resource/20220602/4aa22adb6b854dd89468be60bde83027.png', NULL, '1');
INSERT INTO `blog` VALUES (93, 1, 'Windows 环境下的端口占用', '<p><b><font size=\"5\" color=\"#c24f4a\"><span style=\"font-family: monospace; white-space: pre; background-color: rgb(241, 241, 241);\">输入【netstat -aon</span><span style=\"font-family: monospace; white-space: pre;\">|findstr “端口号”】命令，回车，就可以看到指定端口的占用情况</span></font></b><br/></p><p><br/></p><pre><code class=\"Bash\"><br/></code><code class=\"Bash\">netstat -aon<span class=\"hljs-string\">|findstr </span></code><code class=\"Bash\"><span class=\"hljs-string\"><br/></span></code></pre><p><br/></p><p><b><font size=\"5\" color=\"#c24f4a\"><span style=\"font-family: monospace; white-space: pre; background-color: rgb(241, 241, 241);\">【tasklist</span><span style=\"font-family: monospace; white-space: pre;\">|findstr “被占用端口对应的 PID”】</span></font></b><br/></p><pre><code class=\"Bash\"><br/></code><code class=\"Bash\">tasklist<span class=\"hljs-string\">|findstr </span></code><code class=\"Bash\"><span class=\"hljs-string\"><br/></span></code></pre>', NULL, 55, '2022-12-17 12:02:47', '1', NULL, NULL, '原创', 'Python', '/common/static/resource/20220602/34f904800ed848f1ac65b2a98a171853.png', NULL, '0');
INSERT INTO `blog` VALUES (94, 1, '无需手机号注册QQ方法', '<p style=\"font-family: itzhiyin; margin-top: 0px; margin-bottom: 20px; overflow-wrap: break-word; outline: 0px !important;\"><b style=\"\"><font size=\"4\" style=\"\" color=\"#f9963b\">1.手机搜索下载“TIM”安卓下载地址https://sourl.cn/G4kTQz微信登录-&gt;然后点我的-&gt;右上角设置-&gt;账户管理</font></b></p><p style=\"font-family: itzhiyin; margin-top: 0px; margin-bottom: 20px; overflow-wrap: break-word; outline: 0px !important;\"><b><font size=\"4\" color=\"#f9963b\">2.就可以看到QQ号了-&gt;然后截图保存下QQ号码-&gt;再返回-&gt;设置-&gt;账号安全-&gt;TIM账号密码-&gt;旧密码输入8个空格</font></b></p><p style=\"font-family: itzhiyin; margin-top: 0px; margin-bottom: 20px; overflow-wrap: break-word; outline: 0px !important;\"><b style=\"\"><font size=\"4\" style=\"\" color=\"#f9963b\">3.再输入新密码就可以了-&gt;就可以登录手机电脑QQ了-&gt;注册的都是10位-&gt;玩活动用的上-&gt;注意：很久没登录会冻结</font></b></p><p style=\"font-family: itzhiyin; margin-top: 0px; margin-bottom: 20px; overflow-wrap: break-word; outline: 0px !important;\"><b style=\"\"><font size=\"4\" style=\"\" color=\"#f9963b\"><br/></font></b></p><p style=\"font-family:itzhiyin; margin-top:0px; margin-bottom:20px; overflow-wrap:break-word; outline:0px !important; text-align:center;\"><img src=\"/common/static/blog/20221220/b3dc3c3e346e4088adad36b9e9c5f430.png\" style=\"max-width:100%;\" contenteditable=\"false\"/></p>', NULL, 17, '2022-12-20 10:00:31', NULL, NULL, NULL, '原创', '其他', NULL, NULL, '0');
INSERT INTO `blog` VALUES (95, 1, 'VirtualBox开机自启', '<p><br/></p><pre><code class=\"Bash\">\"C:<span class=\"hljs-command\">\\Program</span> Files<span class=\"hljs-command\">\\Oracle</span><span class=\"hljs-command\">\\VirtualBox</span><span class=\"hljs-command\">\\VBoxManage</span>.exe\" startvm systemd --type headless</code></pre><p><br/></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 24px !important;\"><b style=\"\"><font size=\"3\" style=\"\" color=\"#c24f4a\">备注： centos7 修改为你虚拟机的名称</font></b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 24px !important;\"><b style=\"\"><font size=\"3\" style=\"\" color=\"#c24f4a\">--type headless 是无界面启动的意思</font></b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 24px !important;\"><b style=\"\"><font size=\"3\" style=\"\" color=\"#c24f4a\"><br/></font></b></p><p style=\"outline: 0px; margin-top: 0px; margin-bottom: 16px; overflow: auto hidden; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun; line-height: 24px !important;\"><b><font size=\"4\" color=\"#46acc8\"><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\">装Wi10系统出现OOBEKEYBOARD处理方法：</span><br style=\"content: &quot;&quot;; display: block; width: 705px; height: 0px; margin-top: 15px; margin-bottom: 15px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\"/><br style=\"content: &quot;&quot;; display: block; width: 705px; height: 0px; margin-top: 15px; margin-bottom: 15px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\"/><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\">1、方法1：同时按shift+F10 打开命令窗口，进入到C:/windows/system32/oobe</span><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\">文件夹，输msoobe 回车，然后输入msoobe进入下一步，不用理会错误提示框。按照屏幕提示输入相应的信息直至完成系统的安装即可。</span><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\"></span><br style=\"content: &quot;&quot;; display: block; width: 705px; height: 0px; margin-top: 15px; margin-bottom: 15px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\"/><br style=\"content: &quot;&quot;; display: block; width: 705px; height: 0px; margin-top: 15px; margin-bottom: 15px; font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\"/><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Lantinghei SC&quot;, &quot;Microsoft YaHei&quot;, arial, 宋体, sans-serif, tahoma;\">2、方法2：1）同时按shift+F10 打开命令窗口，输入 CD/ 并按回车键后，返回到根目录，此时屏幕上显示的是 C:/》。2）输入 cd windows/system32/oobe/。 3）输入：msoobe或msoobe.exe。 4）在弹出的安装界面跟着提示下一步即可</span></font></b><b style=\"\"><font size=\"3\" style=\"\" color=\"#c24f4a\"><br/></font></b></p>', NULL, 17, '2022-12-22 22:00:23', NULL, NULL, NULL, '原创', '云计算/大数据', NULL, NULL, '0');
INSERT INTO `blog` VALUES (96, 1, '微信自动阅读点击代码', '<pre><code class=\"Bash\"><span class=\"hljs-keyword\">package</span> org.springblade.modules.atmospherePC.pad;\n\n<span class=\"hljs-keyword\">import</span> lombok.SneakyThrows;\n\n<span class=\"hljs-keyword\">import</span> javax.swing.*;\n<span class=\"hljs-keyword\">import</span> java.awt.*;\n<span class=\"hljs-keyword\">import</span> java.awt.event.ActionEvent;\n<span class=\"hljs-keyword\">import</span> java.awt.event.ActionListener;\n<span class=\"hljs-keyword\">import</span> java.awt.event.KeyEvent;\n<span class=\"hljs-keyword\">import</span> java.util.Random;\n\n<span class=\"hljs-javadoc\">/**\n * Java实现鼠标随机移动\n */</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-class\"><span class=\"hljs-keyword\">class</span> <span class=\"hljs-title\">MouseController</span> <span class=\"hljs-keyword\">implements</span> <span class=\"hljs-title\">Runnable</span> {</span>\n\n	<span class=\"hljs-keyword\">private</span> <span class=\"hljs-keyword\">boolean</span> isStop = <span class=\"hljs-keyword\">false</span>;\n\n\n	<span class=\"hljs-annotation\">@SneakyThrows</span>\n	<span class=\"hljs-annotation\">@Override</span>\n	<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">run</span>() {\n		Robot robot=<span class=\"hljs-keyword\">new</span> Robot();\n\n		<span class=\"hljs-keyword\">int</span> x;\n		<span class=\"hljs-keyword\">int</span> y;\n		Random random = <span class=\"hljs-keyword\">new</span> Random();\n		<span class=\"hljs-keyword\">while</span> (!isStop) {\n			<span class=\"hljs-comment\">//随机生成坐标。</span>\n			x = <span class=\"hljs-number\">1473</span>;  <span class=\"hljs-comment\">// 1000</span>\n			y = <span class=\"hljs-number\">552</span>;  <span class=\"hljs-comment\">//1000</span>\n			<span class=\"hljs-comment\">//开始移动鼠标</span>\n			robot.mouseMove(x, y);\n\n<span class=\"hljs-comment\">//          robot.mousePress(KeyEvent.BUTTON3_DOWN_MASK);     // 模拟按下鼠标右键</span>\n<span class=\"hljs-comment\">//          robot.mouseRelease(KeyEvent.BUTTON3_DOWN_MASK);   // 模拟释放鼠标右键</span>\n\n			<span class=\"hljs-comment\">//鼠标点击</span>\n			robot.mousePress(KeyEvent.BUTTON1_DOWN_MASK);\n			<span class=\"hljs-comment\">//鼠标抬起</span>\n			robot.mouseRelease(KeyEvent.BUTTON1_DOWN_MASK);\n\n			<span class=\"hljs-keyword\">int</span> num=random.nextInt(<span class=\"hljs-number\">20</span>);\n			<span class=\"hljs-comment\">//每5秒一次操作</span>\n			System.out.println(<span class=\"hljs-string\">\"延迟\"</span>+(<span class=\"hljs-number\">6</span>+num)+<span class=\"hljs-string\">\"秒\"</span>);\n			robot.delay(<span class=\"hljs-number\">1000</span>*(<span class=\"hljs-number\">6</span>+num));\n\n		}\n\n	}\n\n\n	<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">static</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">main</span>(String[] args) {\n		MouseController m = <span class=\"hljs-keyword\">new</span> MouseController();\n		m.run();\n<span class=\"hljs-comment\">//		m.getPointInfo();</span>\n	}\n\n	<span class=\"hljs-keyword\">private</span> <span class=\"hljs-keyword\">static</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">getPointInfo</span>(){\n		<span class=\"hljs-keyword\">int</span> x = <span class=\"hljs-number\">0</span>;\n		<span class=\"hljs-keyword\">int</span> y = <span class=\"hljs-number\">0</span>;\n		<span class=\"hljs-keyword\">while</span>(<span class=\"hljs-keyword\">true</span>){\n			PointerInfo pinfo = MouseInfo.getPointerInfo();\n			<span class=\"hljs-keyword\">int</span> mx = pinfo.getLocation().x;\n			<span class=\"hljs-keyword\">int</span> my = pinfo.getLocation().y;\n			<span class=\"hljs-keyword\">if</span> (x!=mx||y!=my){\n				x=mx;\n				y=my;\n				System.out.println(<span class=\"hljs-string\">\"x:\"</span>+mx+<span class=\"hljs-string\">\",y:\"</span>+my);\n			}\n			<span class=\"hljs-keyword\">try</span> {\n				Thread.sleep(<span class=\"hljs-number\">1000</span>);\n			} <span class=\"hljs-keyword\">catch</span> (InterruptedException e) {\n				System.out.println(e.getMessage());\n			}\n		}\n	}\n\n\n}\n\n</code></pre>', NULL, 20, '2023-02-02 08:35:15', NULL, NULL, NULL, '原创', 'java', NULL, NULL, '0');
INSERT INTO `blog` VALUES (97, 1, 'update使用子查询和', '<p><b><font color=\"#c24f4a\" size=\"5\"><span style=\"font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">1， 根据关联</span><a href=\"https://so.csdn.net/so/search?q=%E5%AD%90%E6%9F%A5%E8%AF%A2&amp;spm=1001.2101.3001.7020\" target=\"_blank\" style=\"outline: none; margin-right: 3px; padding-right: 14px; padding-left: 3px; text-decoration-line: none; cursor: pointer; overflow-wrap: break-word; border-radius: 3px; background-image: url(&quot;../img/iconHighlight.svg&quot;); background-position: right top; background-size: 12px 14px; background-repeat: no-repeat; background-attachment: initial; background-origin: initial; background-clip: initial; box-shadow: none; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">子查询</a><span style=\"font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\">，更新字段</span></font></b><br/></p><p><b><font color=\"#c24f4a\" size=\"5\"><span style=\"font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\"><br/></span></font></b></p><pre><code class=\"Bash\"><span class=\"hljs-operator\"><span class=\"hljs-keyword\">Update</span> tab1 t\n\n<span class=\"hljs-keyword\">Set</span> (val1, val2) = (<span class=\"hljs-keyword\">select</span> val1, val2\n\n<span class=\"hljs-keyword\">from</span> tab2\n\n<span class=\"hljs-keyword\">where</span> workdate = t.workdate\n\n<span class=\"hljs-keyword\">and</span> cino = t.cino);</span></code></pre><p><br/></p><p><span style=\"outline: 0px; font-weight: 700; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\"><font size=\"5\" style=\"\" color=\"#c24f4a\">2， 通过非关联子查询，限定更新范围</font></span><br/></p><p><span style=\"outline: 0px; font-weight: 700; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\"><font size=\"5\" style=\"\" color=\"#c24f4a\"><br/></font></span></p><p><span style=\"outline: 0px; font-weight: 700; overflow-wrap: break-word; font-family: -apple-system, &quot;SF UI Text&quot;, Arial, &quot;PingFang SC&quot;, &quot;Hiragino Sans GB&quot;, &quot;Microsoft YaHei&quot;, &quot;WenQuanYi Micro Hei&quot;, sans-serif, SimHei, SimSun;\"></span></p><pre><code class=\"Bash\"><span class=\"hljs-operator\"><span class=\"hljs-keyword\">Update</span> tab1 t\n\n<span class=\"hljs-keyword\">set</span> val1 = <span class=\"hljs-number\">1</span>\n\n<span class=\"hljs-keyword\">where</span> (workdate, cino) <span class=\"hljs-keyword\">in</span> (<span class=\"hljs-keyword\">select</span> workdate, cino <span class=\"hljs-keyword\">from</span> tab2)</span></code></pre>', NULL, 22, '2023-02-06 17:18:27', NULL, NULL, NULL, '原创', '数据库', NULL, NULL, '0');
INSERT INTO `blog` VALUES (99, 1, '「群晖秘籍」群晖使用套件安装Redis', '<div style=\"max-width: 100%; overflow-x: visible; font-size: 18px; line-height: 30px; margin-top: 24px; font-family: arial;\"><ul style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0px; list-style: none; overflow-y: auto; max-width: 100%;\"><li style=\"max-width: 100%; text-align: left;\"><b style=\"\"><font color=\"#c24f4a\"><span style=\"max-width: 100%; --tt-darkmode-color: #A3A3A3;\">在确保已经安装好了</span><span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">第三方套件源</span>后，我们就可以来正式的安装<span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">redis数据库</span>了</font></b></li><li style=\"max-width: 100%; text-align: left;\"><b style=\"\"><font color=\"#c24f4a\"><span style=\"max-width: 100%; --tt-darkmode-color: #A3A3A3;\">在套件中心直接搜索</span><span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">redis</span>并点击<span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">安装</span></font></b></li></ul><p style=\"color: rgb(34, 34, 34); overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><p style=\"color: rgb(34, 34, 34);\"></p><div style=\"max-width: 100%; overflow-x: visible; line-height: 30px; margin-top: 24px;\"><p style=\"color: rgb(34, 34, 34); overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><div style=\"overflow-y: auto; max-width: 100%;\"><p style=\"color: rgb(34, 34, 34); max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"><img src=\"/common/static/blog/20230216/8d8f5235b1214dfc91847d8ea494a3f2.png\" style=\"max-width:100%;\" contenteditable=\"false\"/></p><p style=\"max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"><b style=\"\"><font color=\"#c24f4a\">搜索redis</font></b></p><p style=\"\"></p><p style=\"overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><p style=\"\"></p><div style=\"max-width: 100%; overflow-x: visible; line-height: 30px; margin-top: 24px;\"><p style=\"overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><ul style=\"margin-top: 0px; margin-bottom: 0px; margin-left: 0px; list-style: none; overflow-y: auto; max-width: 100%;\"><li style=\"max-width: 100%; text-align: left;\"><b><font color=\"#c24f4a\"><span style=\"max-width: 100%; --tt-darkmode-color: #A3A3A3;\">等待下载安装完成后点击</span><span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">启动</span>，注意：如果这个时候您去访问<span style=\"max-width: 100%; --tt-darkmode-color: #1E6BB8;\">redis</span>，会发现无法访问！</font></b></li><li style=\"max-width: 100%; text-align: left;\"><span style=\"max-width: 100%; --tt-darkmode-color: #A3A3A3;\"><b style=\"\"><font color=\"#c24f4a\">还多人放弃使用套件而选择使用docker去安装redis就是因为这个原因！</font></b></span></li></ul><p style=\"color: rgb(34, 34, 34); overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><p style=\"color: rgb(34, 34, 34);\"></p><div style=\"max-width: 100%; overflow-x: visible; line-height: 30px; margin-top: 24px;\"><p style=\"color: rgb(34, 34, 34); overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"></p><p style=\"overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; margin-bottom: 0px !important;\"><span style=\"max-width: 100%;\"><span style=\"max-width: 100%; --tt-darkmode-color: #A3A3A3;\"><font color=\"#1c487f\"><b>解决无法访问的问题</b></font></span></span></p><p style=\"color: rgb(34, 34, 34); overflow-y: auto; max-width: 100%; font-size: 18px; line-height: 30px; margin-top: 0px !important; ma