INSERT INTO `blog` VALUES (101, 1, '外置引用js文件获取vue文件中的值', '<p style=\"margin-right: auto; margin-left: auto; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif;\"><b style=\"\"><font size=\"4\" color=\"#c24f4a\">组件index.vue中引入接口文件index.js，接口文件index.js中需要获取组件index.vue日期选择器中的日期 dateSel 来当作接口的日期参数</font></b></p><p style=\"margin-right: auto; margin-left: auto; font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif;\"><b style=\"\"><font size=\"4\" color=\"#c24f4a\">index.vue:</font></b></p><pre><code class=\"Bash\"><span class=\"hljs-number\">1</span> <span class=\"hljs-subst\">&lt;</span>el<span class=\"hljs-attribute\">-date</span><span class=\"hljs-attribute\">-picker</span>\n <span class=\"hljs-number\">2</span>         v<span class=\"hljs-attribute\">-model</span><span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"dateSel\"</span>\n <span class=\"hljs-number\">3</span>         size<span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"mini\"</span>\n <span class=\"hljs-number\">4</span>         <span class=\"hljs-keyword\">type</span><span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"date\"</span>\n <span class=\"hljs-number\">5</span>         :editable<span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"false\"</span>\n <span class=\"hljs-number\">6</span>         :clearable<span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"false\"</span>\n <span class=\"hljs-number\">7</span>         :picker<span class=\"hljs-attribute\">-options</span><span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"pickerOptions\"</span>\n <span class=\"hljs-number\">8</span>         placeholder<span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"选择日期\"</span>\n <span class=\"hljs-number\">9</span>         format<span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"yyyy-MM-dd\"</span>\n<span class=\"hljs-number\">10</span>         value<span class=\"hljs-attribute\">-format</span><span class=\"hljs-subst\">=</span><span class=\"hljs-string\">\"yyyy-MM-dd\"</span>\n<span class=\"hljs-number\">12</span>       <span class=\"hljs-subst\">&lt;</span>/el<span class=\"hljs-attribute\">-date</span><span class=\"hljs-attribute\">-picker</span><span class=\"hljs-subst\">&gt;</span></code></pre><pre><code class=\"Bash\"><span class=\"hljs-tag\">&lt;<span class=\"hljs-title\">script</span>&gt;</span><span class=\"javascript\">import { findResourceUse } from <span class=\"hljs-string\">\'./api/index\'</span>\n<span class=\"hljs-keyword\">const</span> dateApi = <span class=\"hljs-built_in\">require</span>(<span class=\"hljs-string\">\'./api/index\'</span>).default\nexport <span class=\"hljs-keyword\">default</span>({\ndata () {\n    <span class=\"hljs-keyword\">return</span> {\n      dateSel: global.today,\n      pickerOptions: {\n        disabledDate(time) {\n          <span class=\"hljs-keyword\">return</span> time.getTime() &gt; <span class=\"hljs-built_in\">Date</span>.now();\n        }\n      },\n      \n  },\ncreated() {\n    dateApi.sendThis(<span class=\"hljs-keyword\">this</span>)\n  },\n})</span><span class=\"hljs-tag\">&lt;/<span class=\"hljs-title\">script</span>&gt;</span></code></pre><p><br/></p><p><span style=\"font-family: &quot;Helvetica Neue&quot;, Helvetica, Verdana, Arial, sans-serif;\"><b style=\"\"><font size=\"4\" color=\"#c24f4a\">外置引用index.js:</font></b></span><br/></p><pre><code class=\"Bash\"><span class=\"hljs-reserved\">const</span> findResourceUseInfo = `<span class=\"javascript\">${wzUrl}/rmdistributecenter/findResourceUse</span>` <span class=\"hljs-regexp\">//</span> 不重要的接口\n\n<span class=\"hljs-reserved\">let</span> dateS = <span class=\"hljs-literal\">null</span>\n<span class=\"hljs-reserved\">const</span> <span class=\"hljs-function\"><span class=\"hljs-title\">sendThis</span> = <span class=\"hljs-params\">(_this)</span>=&gt;</span> {\n  dateS = _this\n}\n<span class=\"hljs-reserved\">export</span> <span class=\"hljs-reserved\">default</span> {\n  sendThis <span class=\"hljs-regexp\">//</span> 暴露函数\n}\n\n<span class=\"hljs-reserved\">export</span> <span class=\"hljs-reserved\">function</span> findResourceUse (params) {\n  <span class=\"hljs-keyword\">return</span> req({\n    <span class=\"hljs-attribute\">url</span>: `<span class=\"javascript\">${findResourceUseInfo}?orderDate=${dateS.dateSel}</span>`,\n    <span class=\"hljs-attribute\">method</span>: <span class=\"hljs-string\">\'get\'</span>,\n    <span class=\"hljs-attribute\">params</span>: params\n  })\n}</code></pre>', NULL, 19, '2023-02-24 22:59:53', NULL, NULL, NULL, '原创', 'Python', NULL, NULL, '0');
