INSERT INTO `blog` VALUES (104, 1, 'PageCovertUtil', '<pre><code class=\"Java\"><span class=\"hljs-keyword\">package</span> org.jeecg.modules.business.utils;\n\n<span class=\"hljs-keyword\">import</span> cn.hutool.core.bean.BeanUtil;\n<span class=\"hljs-keyword\">import</span> com.baomidou.mybatisplus.core.metadata.IPage;\n<span class=\"hljs-keyword\">import</span> com.baomidou.mybatisplus.extension.plugins.pagination.Page;\n\n<span class=\"hljs-keyword\">import</span> java.util.ArrayList;\n<span class=\"hljs-keyword\">import</span> java.util.List;\n\n<span class=\"hljs-javadoc\">/**\n * IPage&lt;T&gt; 转IPage&lt;B&gt;封装工具类\n *\n *<span class=\"hljs-javadoctag\"> @Author</span>:czx\n *<span class=\"hljs-javadoctag\"> @Date</span> 2021/8/17 17:12\n * www.yimem.com\n */</span>\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-class\"><span class=\"hljs-keyword\">class</span> <span class=\"hljs-title\">PageCovertUtil</span> {</span>\n    <span class=\"hljs-javadoc\">/**\n     * 将PageInfo对象泛型中的Po对象转化为Vo对象\n     *\n     *<span class=\"hljs-javadoctag\"> @param</span> pageInfoPo PageInfo&lt;Po&gt;对象&lt;/&gt;\n     *<span class=\"hljs-javadoctag\"> @param</span> &lt;V&gt;        V类型\n     *<span class=\"hljs-javadoctag\"> @return</span>\n     */</span>\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">static</span> &lt;P,V&gt; IPage&lt;V&gt; <span class=\"hljs-title\">pageVoCovert</span>(IPage&lt;P&gt; pageInfoPo, Class&lt;V&gt; v) {\n        <span class=\"hljs-comment\">// 创建Page对象，实际上是一个ArrayList类型的集合</span>\n        <span class=\"hljs-keyword\">try</span> {\n            <span class=\"hljs-keyword\">if</span> (pageInfoPo != <span class=\"hljs-keyword\">null</span>) {\n\n                IPage&lt;V&gt; page = <span class=\"hljs-keyword\">new</span> Page&lt;&gt;(pageInfoPo.getCurrent(), pageInfoPo.getSize());\n                page.setTotal(pageInfoPo.getTotal());\n                List&lt;P&gt; records = pageInfoPo.getRecords();\n                List&lt;V&gt; list = <span class=\"hljs-keyword\">new</span> ArrayList&lt;&gt;();\n                <span class=\"hljs-keyword\">for</span> (P record : records) {\n                    <span class=\"hljs-keyword\">if</span>(record!=<span class=\"hljs-keyword\">null</span>){\n                        V newV = v.newInstance();\n                        <span class=\"hljs-comment\">// 把原对象数据拷贝到新的对象</span>\n                        BeanUtil.copyProperties(record,newV);\n                        list.add(newV);\n                    }\n                }\n                page.setRecords(list);\n                page.setTotal(pageInfoPo.getTotal());\n                <span class=\"hljs-keyword\">return</span> page;\n            }\n        }<span class=\"hljs-keyword\">catch</span> (Exception e){\n            e.printStackTrace();\n        }\n        <span class=\"hljs-keyword\">return</span> <span class=\"hljs-keyword\">null</span>;\n    }\n}\n</code></pre>', NULL, 13, '2023-06-12 14:37:47', '1', NULL, NULL, '原创', 'Python', '/common/static/resource/20220602/34f904800ed848f1ac65b2a98a171853.png', NULL, '0');
INSERT INTO `blog` VALUES (105, 1, 'BigDecimal加减乘除计算', '<pre><code class=\"Bash\">//加法\nBigDecimal result1 = num1<span class=\"hljs-preprocessor\">.add</span>(num2)<span class=\"hljs-comment\">;</span>\nBigDecimal result12 = num12<span class=\"hljs-preprocessor\">.add</span>(num22)<span class=\"hljs-comment\">;</span>\n//减法\nBigDecimal result2 = num1<span class=\"hljs-preprocessor\">.subtract</span>(num2)<span class=\"hljs-comment\">;</span>\nBigDecimal result22 = num12<span class=\"hljs-preprocessor\">.subtract</span>(num22)<span class=\"hljs-comment\">;</span>\n//乘法\nBigDecimal result3 = num1<span class=\"hljs-preprocessor\">.multiply</span>(num2)<span class=\"hljs-comment\">;</span>\nBigDecimal result32 = num12<span class=\"hljs-preprocessor\">.multiply</span>(num22)<span class=\"hljs-comment\">;</span>\n//绝对值\nBigDecimal result4 = num3<span class=\"hljs-preprocessor\">.abs</span>()<span class=\"hljs-comment\">;</span>\nBigDecimal result42 = num32<span class=\"hljs-preprocessor\">.abs</span>()<span class=\"hljs-comment\">;</span>\n//除法\nBigDecimal result5 = num2<span class=\"hljs-preprocessor\">.divide</span>(num1,<span class=\"hljs-number\">20</span>,BigDecimal<span class=\"hljs-preprocessor\">.ROUND</span>_HALF_UP)<span class=\"hljs-comment\">;</span>\nBigDecimal result52 = num22<span class=\"hljs-preprocessor\">.divide</span>(num12,<span class=\"hljs-number\">20</span>,BigDecimal<span class=\"hljs-preprocessor\">.ROUND</span>_HALF_UP)<span class=\"hljs-comment\">;</span></code></pre>', NULL, 6, '2023-12-18 18:50:59', '3', NULL, NULL, '原创', 'java', '/common/static/resource/20220602/feb0a9a94c2c43c89901289f1a6260f1.png', NULL, '0');
INSERT INTO `blog` VALUES (106, 1, 'bigdecimal类型比较大小', '<pre><code class=\"Bash\">import java.math.BigDecimal;\n\n<span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">class</span> Application {\n    <span class=\"hljs-keyword\">public</span> <span class=\"hljs-keyword\">static</span> <span class=\"hljs-keyword\">void</span> <span class=\"hljs-title\">main</span>(String[] args) {\n        BigDecimal a = <span class=\"hljs-keyword\">new</span> BigDecimal(<span class=\"hljs-number\">10</span>);\n        BigDecimal b = <span class=\"hljs-keyword\">new</span> BigDecimal(<span class=\"hljs-number\">5</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) == <span class=\"hljs-number\">0</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a = b\"</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) == -<span class=\"hljs-number\">1</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a &lt; b\"</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) == <span class=\"hljs-number\">1</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a &gt; b\"</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) != <span class=\"hljs-number\">0</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a != b\"</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) != -<span class=\"hljs-number\">1</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a &gt;= b\"</span>);\n\n        <span class=\"hljs-keyword\">if</span> (a.compareTo(b) != <span class=\"hljs-number\">1</span>)\n            System.<span class=\"hljs-keyword\">out</span>.println(<span class=\"hljs-string\">\"a &lt;= b\"</span>);\n    }\n}\n\n</code></pre>', NULL, 2, '2024-01-28 02:35:11', '5', NULL, NULL, '原创', 'Python', '/common/static/resource/20220602/4aa22adb6b854dd89468be60bde83027.png', NULL, '0');
INSERT INTO `blog` VALUES (107, 1, '按键精灵手机助手连接mumu模拟器的方法，适用于任何模拟器连接按键精灵手机助手', '<p>1、找到按键精灵安装的下面文件夹<br/>D:\\ProgramData\\按键精灵\\按键精灵手机助手\\android</p><p><img src=\"/common/static/blog/20240208/f18a7ffc436443d694da7d8deb8332c2.png\" style=\"max-width:100%;\" contenteditable=\"false\"/><br/>2、找到mumu安装地址的下面文件夹<br/>但是在D:\\Program Files (x86)\\MuMu\\emulator\\nemu\\vmonitor\\bin找到<br/>adb_server.exe<br/>以及另外两个.dll</p><p><img src=\"/common/static/blog/20240208/53e787efc7254945b3952b3b48e09df8.png\" style=\"max-width:100%;\" contenteditable=\"false\"/><br/>3、将此三个文件替换<br/>将mumu模拟器的这三个文件复制到按键精灵上述文件夹，并将这个文件中的adb_server.exe修改为adb.exe<br/><br/>4、进入CMD命令框<br/><img src=\"/common/static/blog/20240208/5127ce9bffce42338a03536342b4b8ee.png\" style=\"max-width:100%;\" contenteditable=\"false\"/>5、输入以下命令即可连接成功<br/><br/>// 输入以下命令即可链接成功<br/><br/></p><pre><code class=\"Bash\"><span class=\"hljs-tag\">adb</span> <span class=\"hljs-tag\">connect</span> 127<span class=\"hljs-class\">.0</span><span class=\"hljs-class\">.0</span><span class=\"hljs-class\">.1</span><span class=\"hljs-pseudo\">:7555</span></code></pre><p><br/><br/>连接成功<br/><br/></p>', NULL, 1, '2024-02-08 11:26:54', NULL, NULL, NULL, '原创', '游戏开发', NULL, NULL, '0');
INSERT INTO `blog` VALUES (108, 1, '常用cron表达式', '<pre><code class=\"Bash\">每隔5秒执行一次：<span class=\"hljs-emphasis\">*/5 *</span> <span class=\"hljs-bullet\">* *</span> * ?\n \n<span class=\"hljs-code\">         每隔1分钟执行一次：0 */1 * * * ?</span>\n \n<span class=\"hljs-code\">         每天23点执行一次：0 0 23 * * ?</span>\n \n<span class=\"hljs-code\">         每天凌晨1点执行一次：0 0 1 * * ?</span>\n \n<span class=\"hljs-code\">         每月1号凌晨1点执行一次：0 0 1 1 * ?</span>\n \n<span class=\"hljs-code\">         每月最后一天23点执行一次：0 0 23 L * ?</span>\n \n<span class=\"hljs-code\">         每周星期天凌晨1点实行一次：0 0 1 ? * L</span>\n \n<span class=\"hljs-code\">         在26分、29分、33分执行一次：0 26,29,33 * * * ?</span>\n \n<span class=\"hljs-code\">         每天的0点、13点、18点、21点都执行一次：0 0 0,13,18,21 * * ?</span></code></pre>', NULL, 1, '2024-02-19 15:33:17', NULL, NULL, NULL, '原创', 'Python', NULL, NULL, '0');
