mapper.select:
select `invitation`.*,`user`.nickname,`user`.headImg from invitation
LEFT JOIN user on `invitation`.userid=`user`.userid


        and invitation.userid=#{userid}


        and title like concat('%',#{title},'%')


        and categoryid = #{categoryid}


        and categoryid2 = #{categoryid2}


        and status =#{status}


ORDER BY createtime,recomment DESC

select COUNT(*) from invitation


            and invitation.userid=#{userid}


            and title like concat('%',#{title},'%')


            and categoryid = #{categoryid}


            and categoryid2 = #{categoryid2}


            and status =#{status}

mapper.insert:
 INSERT INTO invitation (`userid`, `title`, `content`, `status`, `readCount`, `createtime`, `recomment`, `resource`, `lable`, `publishForm`, `categoryid`,`categoryid2`, `img`, `type`) VALUES
 (#{userid}, #{title},#{content},
'1', '0',NOW(), '1', '1', '程序人生', '原创',#{categoryid},#{categoryid2}, NULL, 'BLOG');
